import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Producto } from 'src/app/models/producto';
import { ProductoService } from 'src/app/services/producto.service';

@Component({
  selector: 'app-producto-form',
  templateUrl: './producto-form.component.html',
  styleUrls: ['./producto-form.component.css']
})
export class ProductoFormComponent {
  producto!:Producto;
  constructor(private servicio: ProductoService,private router : Router){
    this.producto = new Producto();
    }

  guardarProducto(){
    this.servicio.createProducto(this.producto).subscribe(
      (result:any)=>{
        if (result.status == 1)
        alert(result.msg)
      },
      error=>{
        alert(error.msg)
      }
    )
  }
}
<div class="card">
    <div class="card-header">
     Agregar un producto
    </div>
    <div class="card-body">
        <form #productoForm="ngForm">
            <div class="mb-3">
                <label for="idnombre" class="form-label">Nombre</label>
                <input name="nombre" type="text" class="form-control" id="idnombre" [(ngModel)]="producto.nombre" required
                     >
              </div>
              <div class="mb-3">
                <label for="iddesc" class="form-label">Descripcion</label>
                <textarea name="desc" class="form-control" id="desc" rows="3" [(ngModel)]="producto.descripcion" required
                    ></textarea>
              </div>
              <div class="mb-3">
                <label for="idprecio" class="form-label">Precio</label>
                <input name="precio" type="number" class="form-control" id="precio" [(ngModel)]="producto.precio" required
                    >
              </div>
              <div class="mb-3">
                <label for="idresponsable" class="form-label">Stock</label>
                <input name="em" type="number" class="form-control" id="il" [(ngModel)]="producto.stock" required> 
                 
              </div>

              <div class="mb-3">
                <label for="idimg" class="form-label">Imagen</label>
                <input name="eme" type="text" class="form-control" id="ideil" [(ngModel)]="producto.imagen" required> 
                 
              </div>

             

              <button type="button" class="btn btn-primary" (click)="guardarProducto()" [disabled]="productoForm.invalid">Guardar</button>              
                            

        </form>
    </div>
</div>
